<?php/* * @category	EDPA * @package		EDPA_Browse_Helper * @author		scottw, ISP * @date        30-09-2015 13:19 * @last edit   16-12-2015 * @copyright	Copyright 2015 EDPA */final class EDPA_Browse_Helper_Colrename{    // encode table name    // @parameter: (string) $name    // @return: (string) $new_name    public function encode($name){        $new_name = strtr(            $name,            array(                "." => '_nn1nn_',                "," => '_oo1oo_',                "-" => '_pp1pp_',                "/" => '_qq1qq_',                "+" => '_rr1rr_',                "£" => '_ss1ss_',                "%" => '_tt1tt_',                "&" => '_uu1uu_',                "?" => '_vv1vv_',                "!" => '_ww1ww_',                "'" => '_xx1xx_',                '(' => '_yy1yy_',                ')' => '_zz1zz_',                ' ' => '_'            )        );        return $new_name;    }    // decode table name    // @parameter: (string) $name    // @return: (string) $new_name    public function decode($name){        $new_name = strtr(            $name,            array(                '_nn1nn_' => ".",                '_oo1oo_' => ",",                '_pp1pp_' => "-",                '_qq1qq_' => "/",                '_rr1rr_' => "+",                '_ss1ss_' => "£",                '_tt1tt_' => "%",                '_uu1uu_' => "&",                '_vv1vv_' => "?",                '_ww1ww_' => "!",                '_xx1xx_' => "'",                '_yy1yy_' => '(',                '_zz1zz_' => ')',                '_' => ' '            )        );        return $new_name;    }}